import React from 'react';
import styled from "styled-components";

const Container = styled.div`
  width: 100%;
  max-width: 500px;
  display: flex;
  flex-direction: column;
  gap: 36px;
`;

const AboutUs = () => {
  return (
    <Container>
     <div className="about-us">
      <h1>About Fitness Tracker</h1>
      <p>
        Fitness Tracker is a comprehensive digital solution designed to empower you to
        take control of your health and fitness journey. We understand that staying
        motivated and achieving your fitness goals can be challenging in today's
        fast-paced world. That's where Fitness Tracker comes in.
      </p>
      <p>
        Our user-friendly app provides you with the tools and resources you need to
        track your progress, set personalized goals, and stay on track. Whether you're
        looking to lose weight, gain muscle, improve your endurance, or simply lead a
        healthier lifestyle, Fitness Tracker can help you get there.
      </p>
      <h2>Here's what sets Fitness Tracker apart:</h2>
      <ul>
        <li>Personalized Goal Setting: Set goals based on your specific needs and aspirations.</li>
        <li>Comprehensive Activity Tracking: Track your workouts and stay motivated.</li>
        <li>Progress Monitoring: Visualize your progress and celebrate your achievements.</li>
        <li>Performance Analysis: Gain insights into your performance and identify areas for improvement.</li>
        <li>User-Friendly Interface: Enjoy a seamless and intuitive experience.</li>
      </ul>
      <p>
        Join the Fitness Tracker community and start your journey to a healthier,
        happier you today! Download the app now and experience the difference.
      </p>
    </div>
    </Container>
  );
};

export default AboutUs;
